/*
    -Una funcion que tome como parametro un vector y su tamañ
    -Y diga si el vector esta ordenado crecientemente
            SUGERENCIA:
                        QUE PARA TODAS LAS POSICIONES DEL VECTOR
                        SALVO PARA LA 0
                            EL ELEMENTO DEL VECTOR ES MAYOR O IGUAL QUE EL ELEMENTO QUE LE PRECEDE EN EL VECTOR|
*/
#include <iostream>
using namespace std;

int vector(int vec[], int);
int main(){

    const int tam = 5;
    int vec[tam] = {5,4,3,2,1};

    vector(vec, tam);

    return 0;
}

int vector(int vec[], int tam){
    for(int i=1; i<tam; i++){
        if(vec[i] >= vec[i]){
            cout<<"Creciente";
        }else{
            cout<<"Error";
        }
    }
}





